x(/[\r\n]?[\t ]*<img .+?toimg\/data\/([^\.]+)[^>]+>[\t ]*[\r\n]?/g,"#$1#");


//去掉标签里的属性
x(/(\<|&lt;)([^\>]*?)(\s|&nbsp;)[^\>]*(\>|&gt;)/g,"<$2>");
